var crypto = require('crypto');

function hash(message){
	return crypto.createHash('sha1').update(message).digest("base64");
}


var MFA = {
	generateOTP: function(domainSeed){
		var timestamp = Math.floor(new Date().getTime()/1000/60/5)+"";console.log(timestamp);
		return hash(domainSeed+timestamp);
	}
};

module.exports = MFA;